import React, { useEffect, useState } from "react";
import axios from "axios";

const MoistureDisplay = () => {
  const [moistureLevel, setMoistureLevel] = useState(null);
  const [moistureStatus, setMoistureStatus] = useState("loading"); // "dry", "wet", "ideal", or "loading"
  const [loading, setLoading] = useState(true);

  // Add these constants for the image URLs
  const dryImage =
    "https://images.unsplash.com/photo-1504544750208-dc0358e63f7f?auto=format&fit=crop&q=80";
  const wetImage =
    "https://images.unsplash.com/photo-1525543907410-b2562b6796d6?auto=format&fit=crop&q=80";
  const idealImage =
    "https://images.unsplash.com/photo-1591857177580-dc82b9ac4e1e?auto=format&fit=crop&q=80";

  useEffect(() => {
    const fetchLatestData = async () => {
      try {
        const response = await axios.get(
          "http://localhost:8081/api/sensor-data"
        );

        if (response.data && response.data.length > 0) {
          const latestReading = response.data[0];
          const moistureValue = latestReading.moistureLevel;

          setMoistureLevel(moistureValue);

          // Define moisture thresholds
          // You may need to adjust these based on your sensor's range
          if (moistureValue < 300) {
            setMoistureStatus("dry");
          } else if (moistureValue > 700) {
            setMoistureStatus("wet");
          } else {
            setMoistureStatus("ideal");
          }

          setLoading(false);
        }
      } catch (err) {
        console.error("Error fetching moisture data:", err);
        setLoading(false);
      }
    };

    // Fetch data immediately
    fetchLatestData();

    // Then fetch every 3 seconds
    const interval = setInterval(fetchLatestData, 3000);

    // Clean up the interval on component unmount
    return () => clearInterval(interval);
  }, []);

  // Get the appropriate image and message based on moisture status
  const getContent = () => {
    switch (moistureStatus) {
      case "dry":
        return {
          image: dryImage,
          message: "TOO DRY",
          color: "text-red-600",
          description: "Your soil is too dry for planting. Water it more!",
        };
      case "wet":
        return {
          image: wetImage,
          message: "TOO WET",
          color: "text-blue-600",
          description: "Your soil is too wet for planting. Let it dry out!",
        };
      case "ideal":
        return {
          image: idealImage,
          message: "PERFECT",
          color: "text-green-600",
          description:
            "Your soil has ideal moisture for planting. Perfect time to plant!",
        };
      default:
        return {
          image: null,
          message: "Loading...",
          color: "text-gray-600",
          description: "Getting the latest soil moisture data...",
        };
    }
  };

  const content = getContent();

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
      {/* Moisture value display */}
      <div className="text-6xl font-bold mb-6">
        {loading ? "..." : moistureLevel}
      </div>

      {/* Image and status */}
      <div className="w-full max-w-2xl bg-white rounded-lg shadow-lg overflow-hidden">
        {content.image && (
          <div className="relative">
            <img
              src={content.image}
              alt={content.message}
              className="w-full h-64 sm:h-80 object-cover"
            />
            <div className="absolute inset-0 bg-black bg-opacity-40 flex items-center justify-center">
              <h2
                className={`text-5xl sm:text-6xl font-bold ${content.color} bg-white bg-opacity-80 px-6 py-3 rounded-lg`}
              >
                {content.message}
              </h2>
            </div>
          </div>
        )}

        <div className="p-6">
          <p className="text-xl">{content.description}</p>

          {!loading && (
            <div className="mt-4 pt-4 border-t border-gray-200">
              <p className="text-gray-600">
                Current moisture reading: {moistureLevel}
                <span className="block text-sm text-gray-500 mt-1">
                  Updated at {new Date().toLocaleTimeString()}
                </span>
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MoistureDisplay;
